<template>
  <!-- outline container -->
  <div class="container">
    <!-- drink menu bar -->
    <div id="menubar">
      <div class="menu" :key="i" v-for="(drink, i) in drinkList">
        <button @click="requestDrink(drink.drinkId)">
          <img class="menuimg" :src="drink.imgsrc" /><br />
          {{ drink.drinkName }}<br />
          가격 :{{ drink.price }}<br />
          재고 :{{ drink.stock }}
        </button>
      </div>
    </div>

    <!-- input coin bar  -->
    <div id="coinbar">
      <div class="div-coin">
        <h2>동전 넣기</h2>
        <button
          class="btn-coin"
          @click="inputCoin(coin)"
          :key="coin"
          v-for="coin in coinList"
        >
          {{ coin }} 원 투입
        </button>
      </div>
    </div>

    <div id="statusbar">
      <!-- if not -->
      <h2 v-show="msg != ''">{{ msg }}</h2>

      <div class="status">
        <label for="">
          지갑 잔고
          <input type="text" v-model="total_customer_amount" readonly />
        </label>
      </div>

      <div class="status">
        <label for="">
          자판기 잔고
          <input type="text" v-model="total_vending_amount" readonly />
        </label>
      </div>

      <div class="status">
        <label for="">
          현재 투입 금액
          <input type="text" v-model="total_input_amount" readonly />
        </label>
      </div>
    </div>
  </div>
</template>
<script>
export default {
  components: {},
  data() {
    return {
      total_customer_amount: 10000,
      total_vending_amount: 1000,
      total_input_amount: 0,
      msg: "",
      // initiation status
      coinList: [100, 500, 1000],
      drinkList: [
        {
          drinkId: "1",
          drinkName: "코카콜라",
          price: 700,
          stock: 5,
          imgsrc:
            "data:image/jpeg;base64,",
        },
        {
          drinkId: "2",
          drinkName: "오렌지주스",
          price: 1200,
          stock: 5,
          imgsrc:
            "data:image/jpeg;base64,",
        },
        {
          drinkId: "3",
          drinkName: "커피",
          price: 500,
          stock: 5,
          imgsrc:
            "data:image/jpeg;base64,",
        },
        {
          drinkId: "4",
          drinkName: "물",
          price: 700,
          stock: 5,
          imgsrc:
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRl9FM7lmOReLz3sVvxXd1PMw0dduZTr8R31w&usqp=CAU",
        },
        {
          drinkId: "5",
          drinkName: "보리차",
          price: 1200,
          stock: 5,
          imgsrc:
            "data:image/jpeg;base64,",
        },
        {
          drinkId: "6",
          drinkName: "포카리",
          price: 1000,
          stock: 5,
          imgsrc:
            "data:image/jpeg;base64,",
        },
        {
          drinkId: "7",
          drinkName: "뽀로로",
          price: 1300,
          stock: 5,
          imgsrc:
            "data:image/jpeg;base64,",
        },
      ],
    };
  },
  setup() {},
  created() {},
  mounted() {},
  unmounted() {},
  methods: {
    inputCoin(coin) {
      if (this.total_customer_amount - coin >= 0) {
        this.total_customer_amount -= coin;
        this.total_vending_amount += coin;
        this.total_input_amount += coin;
      } else {
        var msg = "지갑에 돈이 부족합니다!";
        alert(msg);
        this.msg = msg;
      }
    },

    requestDrink(drinkId) {
      let drink = this.drinkList.filter((d) => d.drinkId == drinkId)[0];

      //   console.log(drink.price);
      //   console.log(drink.drinkName);
      var msg = "";

      // 조건문 순서 : 재고 확인 => 가격 확인 => 거스름돈 확인
      // 재고가 있는 경우
      if (drink.stock > 0) {
        // 가격 확인 : 현재 투입금액이 제품가격보다 같거나 큰 경우
        if (this.total_input_amount >= drink.price) {
          var exchangeAmount = this.total_input_amount - drink.price;

          // 잔금 확인 : 잔금을 돌려줄 수 있는 경우
          if (this.total_vending_amount >= exchangeAmount) {
            // 거래 성사
            // 거래 성사 시 작동되어야 할 것
            // 1. 재고 감소 2. 잔금 반환 3. 제품 반출
            // 4. 투입금액 초기화
            drink.stock--;

            this.total_customer_amount += exchangeAmount;
            this.total_vending_amount -= exchangeAmount;
            this.total_input_amount = 0;

            // 잔금 확인 : 잔금을 돌려줄 수 없는 경우
          } else {
            msg = "죄송합니다, 현재 기계의 잔금이 부족합니다";
            alert(msg);
            this.msg = msg;
          }
          // 가격 확인 : 현재 투입금액이 제품가격보다 적은 경우
        } else {
          msg = "삐빅! 투입금액이 부족합니다. 돈을 더 넣어주세요";
          alert(msg);
          this.msg = msg;
        }
      } else {
        // 재고가 없는 경우

        msg = "죄송합니다, 재고가 부족합니다";
        alert(msg);
        this.msg = msg;
      }
    },
  },
};
</script>

